// src/Media/MediaLister.js
import React, { useState, useEffect } from 'react';
import MediaService from './MediaService';

const MediaLister = () => {
    const [medias, setMedias] = useState([]);
    const [filter, setFilter] = useState({ name: '', genre: '', rating: '' });
    const [sortConfig, setSortConfig] = useState({ key: '', direction: 'asc' });

    useEffect(() => {
        fetchMedias();
    }, [filter, sortConfig]);

    const fetchMedias = async () => {
        const response = await MediaService.getAllMedias();
        let filteredData = response.data
            .filter(media => {
                return (
                    media.name.toLowerCase().includes(filter.name.toLowerCase()) &&
                    media.genre.toLowerCase().includes(filter.genre.toLowerCase()) &&
                    (filter.rating ? media.rating === parseInt(filter.rating) : true)
                );
            })
            .sort((a, b) => {
                if (sortConfig.key) {
                    const order = sortConfig.direction === 'asc' ? 1 : -1;
                    return a[sortConfig.key] > b[sortConfig.key] ? order : -order;
                }
                return 0;
            });
        setMedias(filteredData);
    };

    const handleFilterChange = (e) => {
        const { name, value } = e.target;
        setFilter(prev => ({ ...prev, [name]: value }));
    };

    const handleSort = (key) => {
        setSortConfig((prev) => ({
            key,
            direction: prev.direction === 'asc' ? 'desc' : 'asc',
        }));
    };

    return (
        <div className="container">
            <div className="list-header">
                <div className="filter-group">
                    <input
                        type="text"
                        placeholder="Filter by Name"
                        name="name"
                        value={filter.name}
                        onChange={handleFilterChange}
                        className="filter-input"
                    />
                    <input
                        type="text"
                        placeholder="Filter by Genre"
                        name="genre"
                        value={filter.genre}
                        onChange={handleFilterChange}
                        className="filter-input"
                    />
                    <input
                        type="number"
                        placeholder="Filter by Rating"
                        name="rating"
                        value={filter.rating}
                        onChange={handleFilterChange}
                        className="filter-input"
                    />
                </div>
                <button className="modify-button" onClick={() => handleSort('name')}>Name ⬍</button>
                <button className="modify-button" onClick={() => handleSort('rating')}>Rating ⬍</button>
                <button className="modify-button" onClick={() => handleSort('genre')}>Genre ⬍</button>
            </div>
            {medias.map((media) => (
                <div className="table-row" key={media.mediaId}>
                    <div>{media.name}</div>
                    <div>{media.genre}</div>
                    <div>{media.rating}</div>
                    <div className="button-group">
                        <button className="modify-button">Edit</button>
                        <button className="delete-button">Delete</button>
                    </div>
                </div>
            ))}
        </div>
    );
};

export default MediaLister;
