import React, { useState } from 'react';
import ClientsService from './ClientsService';

function ClientAdder() {
  const [client, setClient] = useState({
    name: '',
    address: '',
    email: '',
    phoneNumber: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setClient((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    ClientsService.create(client)
      .then(() => alert('Client added successfully'))
      .catch((error) => console.error(error));
  };

  return (
    <form onSubmit={handleSubmit}>
      <input name="name" placeholder="Name" maxLength="15" required onChange={handleChange} />
      <input name="address" placeholder="Address" required onChange={handleChange} />
      <input name="email" type="email" placeholder="Email" maxLength="50" required onChange={handleChange} />
      <input
        name="phoneNumber"
        type="number"
        placeholder="Phone Number"
        required
        onChange={handleChange}
      />
      <button type="submit">Add Client</button>
    </form>
  );
}

export default ClientAdder;
