import React, { useState } from 'react';
import BorrowsService from './BorrowService';

function BorrowInitiator() {
  const [borrow, setBorrow] = useState({
    clientId: '',
    mediaId: '',
    borrowDate: '',
    returnDate: '',
    returned: false,
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setBorrow((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    BorrowsService.create(borrow)
      .then(() => alert('Borrow initiated successfully'))
      .catch((error) => console.error(error));
  };

  return (
    <form onSubmit={handleSubmit}>
      <input name="clientId" placeholder="Client ID" required onChange={handleChange} />
      <input name="mediaId" placeholder="Media ID" required onChange={handleChange} />
      <input
        name="borrowDate"
        type="date"
        placeholder="Borrow Date"
        required
        onChange={handleChange}
      />
      <input
        name="returnDate"
        type="date"
        placeholder="Return Date"
        required
        onChange={handleChange}
      />
      <label>
        Returned:
        <input
          name="returned"
          type="checkbox"
          onChange={() => setBorrow((prev) => ({ ...prev, returned: !prev.returned }))}
        />
      </label>
      <button type="submit">Initiate Borrow</button>
    </form>
  );
}

export default BorrowInitiator;
