import React, { useEffect, useState } from 'react';
import { useParams, useHistory } from 'react-router-dom';
import BorrowsService from './BorrowService';

const BorrowUpdate = () => {
  const { id } = useParams(); // Get the borrow ID from the URL
  const history = useHistory();
  const [borrow, setBorrow] = useState({
    clientId: '',
    mediaId: '',
    borrowDate: '',
    returnDate: '',
    returned: false,
  });

  useEffect(() => {
    const fetchBorrow = async () => {
      try {
        const { data } = await BorrowsService.getById(id);
        setBorrow(data); // Set the state with the fetched borrow data
      } catch (error) {
        console.error('Error fetching borrow: ', error);
        alert('Error fetching borrow details');
      }
    };
    fetchBorrow();
  }, [id]);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setBorrow({
      ...borrow,
      [name]: type === 'checkbox' ? checked : value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await BorrowsService.update(id, borrow); // Send updated data to the backend
      alert('Borrow updated successfully!');
      history.push('/borrows'); // Redirect to the borrow list page
    } catch (error) {
      console.error('Error updating borrow: ', error);
      alert('Error updating borrow');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        name="clientId"
        placeholder="Client ID"
        value={borrow.clientId}
        onChange={handleChange}
      />
      <input
        name="mediaId"
        placeholder="Media ID"
        value={borrow.mediaId}
        onChange={handleChange}
      />
      <input
        name="borrowDate"
        type="datetime-local"
        value={borrow.borrowDate}
        onChange={handleChange}
      />
      <input
        name="returnDate"
        type="datetime-local"
        value={borrow.returnDate}
        onChange={handleChange}
      />
      <label>
        Returned:
        <input
          name="returned"
          type="checkbox"
          checked={borrow.returned}
          onChange={handleChange}
        />
      </label>
      <button type="submit">Update Borrow</button>
    </form>
  );
};

export default BorrowUpdate;
