// src/Media/MediaAdder.js
import React, { useState } from 'react';
import MediaService from './MediaService';

const MediaAdder = () => {
    const [media, setMedia] = useState({ name: '', description: '', genre: '', rating: '', duration: '', stock: '' });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setMedia((prevMedia) => ({ ...prevMedia, [name]: value }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        MediaService.addMedia(media).then(() => {
            alert('Media added successfully');
            window.location.href = '/media/list';
        });
    };

    return (
        <div className="add-media-form">
            <form onSubmit={handleSubmit}>
                <label>Name</label>
                <input name="name" value={media.name} onChange={handleChange} required />

                <label>Description</label>
                <input name="description" value={media.description} onChange={handleChange} required />

                <label>Genre</label>
                <input name="genre" value={media.genre} onChange={handleChange} required />

                <label>Rating</label>
                <input name="rating" type="number" min="1" max="10" value={media.rating} onChange={handleChange} required />

                <label>Duration</label>
                <input name="duration" type="number" value={media.duration} onChange={handleChange} required />

                <label>Stock</label>
                <input name="stock" type="number" value={media.stock} onChange={handleChange} required />

                <button type="submit">Add Media</button>
            </form>
        </div>
    );
};

export default MediaAdder;
