import React, { useState } from 'react';
import MediasService from './MediaService';

function MediaAdder() {
  const [media, setMedia] = useState({
    name: '',
    description: '',
    genre: '',
    rating: 1,
    type: 'DVD',
    duration: 0,
    stock: 0,
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setMedia((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    MediasService.create(media)
      .then(() => alert('Media added successfully'))
      .catch((error) => console.error(error));
  };

  return (
    <form onSubmit={handleSubmit}>
      <input name="name" placeholder="Name" maxLength="15" required onChange={handleChange} />
      <textarea name="description" placeholder="Description" required onChange={handleChange} />
      <input name="genre" placeholder="Genre" required onChange={handleChange} />
      <input
        name="rating"
        type="number"
        placeholder="Rating (1-10)"
        min="1"
        max="10"
        required
        onChange={handleChange}
      />
      <select name="type" required onChange={handleChange}>
        <option value="DVD">DVD</option>
        <option value="VHS">VHS</option>
        <option value="BluRay">BluRay</option>
        <option value="CD">CD</option>
        <option value="Vinyl">Vinyl</option>
        <option value="Cassette">Cassette</option>
        <option value="Digital">Digital</option>
        <option value="Other">Other</option>
      </select>
      <input
        name="duration"
        type="number"
        placeholder="Duration (minutes)"
        min="0"
        required
        onChange={handleChange}
      />
      <input
        name="stock"
        type="number"
        placeholder="Stock (0-100)"
        min="0"
        max="100"
        required
        onChange={handleChange}
      />
      <button type="submit">Add Media</button>
    </form>
  );
}

export default MediaAdder;
