import React, { useEffect, useState } from 'react';
import { useParams, useHistory } from 'react-router-dom';
import MediasService from './MediasService';

const MediaUpdate = () => {
  const { id } = useParams();
  const history = useHistory();
  const [media, setMedia] = useState({
    Name: '',
    Description: '',
    Genre: '',
    Rating: '',
    Duration: '',
    Stock: '',
  });

  useEffect(() => {
    const fetchMedia = async () => {
      try {
        const response = await MediasService.getById(id);
        setMedia(response.data);
      } catch (error) {
        console.error('Error fetching media: ', error);
      }
    };
    fetchMedia();
  }, [id]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setMedia({
      ...media,
      [name]: value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await MediasService.update(id, media);
      alert('Media updated successfully!');
      history.push('/media/list');
    } catch (error) {
      console.error('Error updating media: ', error);
      alert('Error updating media');
    }
  };

  return (
    <div>
      <h2>Update Media</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Name</label>
          <input type="text" name="Name" value={media.Name} onChange={handleChange} />
        </div>
        <div>
          <label>Description</label>
          <input type="text" name="Description" value={media.Description} onChange={handleChange} />
        </div>
        <div>
          <label>Genre</label>
          <input type="text" name="Genre" value={media.Genre} onChange={handleChange} />
        </div>
        <div>
          <label>Rating</label>
          <input type="number" name="Rating" value={media.Rating} onChange={handleChange} />
        </div>
        <div>
          <label>Duration</label>
          <input type="number" name="Duration" value={media.Duration} onChange={handleChange} />
        </div>
        <div>
          <label>Stock</label>
          <input type="number" name="Stock" value={media.Stock} onChange={handleChange} />
        </div>
        <button type="submit">Update Media</button>
      </form>
    </div>
  );
};

export default MediaUpdate;
