import React, { useEffect, useState } from 'react';
import BorrowsService from './BorrowService';

function BorrowLister() {
  const [borrows, setBorrows] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [sortKey, setSortKey] = useState('borrowDate');
  const [filter, setFilter] = useState({ clientId: '', mediaId: '', returned: '' });

  useEffect(() => {
    BorrowsService.getAll().then((response) => setBorrows(response.data));
  }, []);

  const handleSearch = (e) => setSearchTerm(e.target.value.toLowerCase());

  const handleSort = (key) => setSortKey(key);

  const handleFilter = (e) => {
    const { name, value } = e.target;
    setFilter((prev) => ({ ...prev, [name]: value }));
  };

  const filteredBorrows = borrows
    .filter((borrow) =>
      (!filter.clientId || borrow.clientId.includes(filter.clientId)) &&
      (!filter.mediaId || borrow.mediaId.includes(filter.mediaId)) &&
      (!filter.returned || borrow.returned.toString() === filter.returned)
    )
    .sort((a, b) => (a[sortKey] > b[sortKey] ? 1 : -1));

  return (
    <div>
      <div style={{ display: 'flex', justifyContent: 'space-between' }}>
        <input placeholder="Search by client ID" onChange={handleSearch} />
        <button onClick={() => window.location.href = '/borrow/new'}>Add New Borrow</button>
      </div>
      <div>
        <input
          name="clientId"
          placeholder="Filter by Client ID"
          onChange={handleFilter}
        />
        <input
          name="mediaId"
          placeholder="Filter by Media ID"
          onChange={handleFilter}
        />
        <select name="returned" onChange={handleFilter}>
          <option value="">All</option>
          <option value="true">Returned</option>
          <option value="false">Not Returned</option>
        </select>
        <select onChange={(e) => handleSort(e.target.value)}>
          <option value="borrowDate">Sort by Borrow Date</option>
          <option value="returnDate">Sort by Return Date</option>
        </select>
      </div>
      <ul>
        {filteredBorrows.map((borrow) => (
          <li key={borrow.borrowId}>
            Client ID: {borrow.clientId} - Media ID: {borrow.mediaId} - Borrowed: {borrow.borrowDate} - Returned: {borrow.returned ? 'Yes' : 'No'}
            <button onClick={() => window.location.href = `/borrow/edit/${borrow.borrowId}`}>Modify</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default BorrowLister;
