import React, { useState, useEffect } from 'react';
import BorrowService from './BorrowService'; // Adjust the import based on your project structure
import ClientService from './ClientService';
import MediaService from './MediaService';

const BorrowLister = () => {
  const [borrows, setBorrows] = useState([]);
  const [clients, setClients] = useState([]);
  const [medias, setMedias] = useState([]);
  const [filters, setFilters] = useState({
    borrowDate: '',
    returnDate: ''
  });

  const fetchBorrows = () => {
    BorrowService.getAll(filters)
      .then(response => setBorrows(response.data))
      .catch(error => console.error(error));
  };

  useEffect(() => {
    fetchBorrows();
    ClientService.getAll().then(response => setClients(response.data));
    MediaService.getMedias().then(response => setMedias(response.data));
  }, [filters]);

  return (
    <div className="list-page">
      {/* Filter Controls */}
      <div className="filter-container">
        <div className="filter-box">
          <label className="filter-label">Search by Borrow Date</label>
          <input
            type="date"
            className="filter-input"
            value={filters.borrowDate}
            onChange={e => setFilters({ ...filters, borrowDate: e.target.value })}
          />
        </div>
        <div className="filter-box">
          <label className="filter-label">Search by Return Date</label>
          <input
            type="date"
            className="filter-input"
            value={filters.returnDate}
            onChange={e => setFilters({ ...filters, returnDate: e.target.value })}
          />
        </div>
        <button className="sort-button" onClick={fetchBorrows}>
          Apply Filters
        </button>
      </div>

      {/* Table */}
      <div className="table">
        {borrows.map(borrow => {
          const client = clients.find(c => c.clientId === borrow.clientId);
          const media = medias.find(m => m.mediaId === borrow.mediaId);

          return (
            <div key={borrow.borrowId} className="table-row">
              <div className="form-field">{client ? client.name : 'N/A'}</div>
              <div className="form-field">{media ? media.name : 'N/A'}</div>
              <div className="form-field">{borrow.borrowDate}</div>
              <div className="form-field">{borrow.returnDate}</div>
              <div className="form-field">
                <button className="modify-button">Edit</button>
                <button className="delete-button">Delete</button>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default BorrowLister;
