import React, { useState, useEffect } from 'react';
import BorrowService from './BorrowService'; // Adjust the import based on your project structure
import ClientService from './ClientService';
import MediaService from './MediaService';

const BorrowLister = () => {
  const [borrows, setBorrows] = useState([]);
  const [clients, setClients] = useState([]);
  const [medias, setMedias] = useState([]);
  const [filters, setFilters] = useState({
    borrowDate: '',
    returnDate: ''
  });
  const [sortOrder, setSortOrder] = useState('asc'); // Default sort order

  const fetchBorrows = () => {
    BorrowService.getBorrows(filters, sortOrder)
      .then(response => setBorrows(response.data))
      .catch(error => console.error(error));
  };

  useEffect(() => {
    fetchBorrows();
    ClientService.getAll().then(response => setClients(response.data));
    MediaService.getAll().then(response => setMedias(response.data));
  }, [filters, sortOrder]);

  const handleDelete = (borrowId) => {
    BorrowService.deleteBorrow(borrowId)
      .then(() => {
        // Remove the deleted borrow from state (UI update)
        setBorrows(borrows.filter(borrow => borrow.borrowId !== borrowId));
      })
      .catch(error => console.error("Error deleting borrow:", error));
  };

  return (
    <div className="list-page">
      {/* Filter Controls */}
      <div className="filter-container">
        <div className="filter-box">
          <label className="filter-label">Search by Borrow Date</label>
          <input
            type="date"
            className="filter-input"
            value={filters.borrowDate}
            onChange={e => setFilters({ ...filters, borrowDate: e.target.value })}
          />
        </div>
        <div className="filter-box">
          <label className="filter-label">Search by Return Date</label>
          <input
            type="date"
            className="filter-input"
            value={filters.returnDate}
            onChange={e => setFilters({ ...filters, returnDate: e.target.value })}
          />
        </div>
        <button className="sort-button" onClick={() => setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc')}>
          {sortOrder === 'asc' ? 'Sort Descending' : 'Sort Ascending'}
        </button>
      </div>

      {/* Table */}
      <div className="table">
        {borrows.map(borrow => (
          <div key={borrow.borrowId} className="table-row">
            <div className="form-field">{borrow.borrowDate}</div>
            <div className="form-field">{borrow.returnDate}</div>
            <div className="form-field">{clients.find(client => client.clientId === borrow.clientId)?.name}</div>
            <div className="form-field">{medias.find(media => media.mediaId === borrow.mediaId)?.name}</div>
            <div className="form-field">
              <button className="modify-button">Edit</button>
              <button className="delete-button" onClick={() => handleDelete(borrow.borrowId)}>Delete</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default BorrowLister;
