import React, { useEffect, useState } from 'react';
import { useParams, useHistory } from 'react-router-dom';
import ClientsService from './ClientsService';  // import ClientsService

const ClientUpdate = () => {
  const { id } = useParams();
  const history = useHistory();
  const [client, setClient] = useState({
    Name: '',
    Address: '',
    Email: '',
    PhoneNumber: '',
  });

  useEffect(() => {
    const fetchClient = async () => {
      try {
        const { data } = await ClientsService.getById(id);  // Fetch client using ClientsService
        setClient(data);
      } catch (error) {
        console.error('Error fetching client: ', error);
      }
    };
    fetchClient();
  }, [id]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setClient({
      ...client,
      [name]: value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await ClientsService.update(id, client);  // Update the client data using ClientsService
      alert('Client updated successfully!');
      history.push('/clients');  // Redirect to the clients list page
    } catch (error) {
      console.error('Error updating client: ', error);
      alert('Error updating client');
    }
  };

  return (
    <div>
      <h2>Update Client</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Name</label>
          <input
            type="text"
            name="Name"
            value={client.Name}
            onChange={handleChange}
          />
        </div>
        <div>
          <label>Address</label>
          <input
            type="text"
            name="Address"
            value={client.Address}
            onChange={handleChange}
          />
        </div>
        <div>
          <label>Email</label>
          <input
            type="email"
            name="Email"
            value={client.Email}
            onChange={handleChange}
          />
        </div>
        <div>
          <label>Phone Number</label>
          <input
            type="text"
            name="PhoneNumber"
            value={client.PhoneNumber}
            onChange={handleChange}
          />
        </div>
        <button type="submit">Update Client</button>
      </form>
    </div>
  );
};

export default ClientUpdate;
