import React, { useState, useEffect } from 'react';
import MediaService from './MediaService';
import './ListPage.css';

const MediaLister = () => {
    const [mediaList, setMediaList] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');
    const [sortConfig, setSortConfig] = useState({ key: '', direction: 'ascending' });

    useEffect(() => {
        MediaService.getAllMedia().then((response) => setMediaList(response.data));
    }, []);

    const sortedMedia = [...mediaList].sort((a, b) => {
        if (sortConfig.key) {
            if (a[sortConfig.key] < b[sortConfig.key]) return sortConfig.direction === 'ascending' ? -1 : 1;
            if (a[sortConfig.key] > b[sortConfig.key]) return sortConfig.direction === 'ascending' ? 1 : -1;
        }
        return 0;
    }).filter(media => media.name.toLowerCase().includes(searchTerm.toLowerCase()));

    const handleSort = (key) => {
        setSortConfig({
            key,
            direction: sortConfig.direction === 'ascending' ? 'descending' : 'ascending'
        });
    };

    return (
        <div className="list-page">
            <div className="header-row">
                <input
                    type="text"
                    placeholder="Search..."
                    onChange={(e) => setSearchTerm(e.target.value)}
                />
                <button onClick={() => window.location.href = '/media/add'}>Add New Media</button>
            </div>
            <div className="table">
                <div className="table-header">
                    <div onClick={() => handleSort('name')}>Name &#x2195;</div>
                    <div onClick={() => handleSort('genre')}>Genre &#x2195;</div>
                    <div onClick={() => handleSort('rating')}>Rating &#x2195;</div>
                    <div>Actions</div>
                </div>
                {sortedMedia.map((media) => (
                    <div className="table-row" key={media.mediaId}>
                        <div>{media.name}</div>
                        <div>{media.genre}</div>
                        <div>{media.rating}</div>
                        <div>
                            <button onClick={() => handleDelete(media.mediaId)}>Delete</button>
                            <button onClick={() => window.location.href = `/media/update/${media.mediaId}`}>Edit</button>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default MediaLister;
