// src/Borrow/BorrowLister.js
import React, { useState, useEffect } from 'react';
import BorrowService from './BorrowService';
import ListHeader from '../components/Listheader';
import '../styles/ListPage.css';

const BorrowLister = () => {
    const [borrows, setBorrows] = useState([]);

    useEffect(() => {
        BorrowService.getAllBorrows().then((response) => setBorrows(response.data));
    }, []);

    return (
        <div className="list-page">
            <ListHeader
                headers={[
                    { key: 'clientName', label: 'Client' },
                    { key: 'mediaName', label: 'Media' },
                    { key: 'borrowDate', label: 'Borrow Date' },
                ]}
                onSort={() => {}}
                onFilter={() => {}}
            />
            <div className="table">
                {borrows.map((borrow) => (
                    <div className="table-row" key={borrow.borrowId}>
                        <div>{borrow.clientName}</div>
                        <div>{borrow.mediaName}</div>
                        <div>{new Date(borrow.borrowDate).toLocaleDateString()}</div>
                        <div>
                            <button onClick={() => BorrowService.deleteBorrow(borrow.borrowId)}>Delete</button>
                            <button onClick={() => window.location.href = `/borrow/update/${borrow.borrowId}`}>Edit</button>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default BorrowLister;
