import React, { useState, useEffect } from 'react';
import ListHeader from '../components/ListHeader'; // Adjust the path as necessary
import MediaService from './MediaService'; // Adjust the path if needed

const MediaLister = () => {
  const [medias, setMedias] = useState([]);
  const [filters, setFilters] = useState({});
  const [sortConfig, setSortConfig] = useState({ key: '', direction: 'asc' });

  useEffect(() => {
    // Fetch media data with filters and sorting applied
    MediaService.getAll(filters, sortConfig)
      .then(response => setMedias(response.data))
      .catch(error => console.error(error));
  }, [filters, sortConfig]);

  // Handle filtering input changes
  const handleFilter = (key, value) => {
    setFilters((prev) => ({ ...prev, [key]: value }));
  };

  // Handle sorting changes (toggle between ascending and descending)
  const handleSort = (key, direction) => {
    setSortConfig({ key, direction });
  };

  // Define the table headers
  const headers = [
    { key: 'name', label: 'Name' },
    { key: 'genre', label: 'Genre' },
    { key: 'rating', label: 'Rating' },
    { key: 'duration', label: 'Duration' },
    { key: 'stock', label: 'Stock' },
    // Add other columns as needed
  ];

  return (
    <div className="list-page">
      {/* List Header with Sorting and Filtering */}
      <ListHeader headers={headers} onSort={handleSort} onFilter={handleFilter} />

      {/* Media Table */}
      <div className="table">
        {medias.length > 0 ? (
          medias.map(media => (
            <div key={media.mediaId} className="table-row">
              <div>{media.name}</div>
              <div>{media.genre}</div>
              <div>{media.rating}</div>
              <div>{media.duration}</div>
              <div>{media.stock}</div>
              {/* Add other columns as needed */}
            </div>
          ))
        ) : (
          <div>No media found.</div>
        )}
      </div>
    </div>
  );
};

export default MediaLister;
