import React, { useEffect, useState } from 'react';
import BorrowService from './BorrowService';

const BorrowLister = () => {
  const [borrows, setBorrows] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [sortKey, setSortKey] = useState('borrowDate');
  const [filter, setFilter] = useState({ clientId: '', mediaId: '' });

  useEffect(() => {
    BorrowService.getAll().then((response) => setBorrows(response.data));
  }, []);

  const handleSearch = (e) => setSearchTerm(e.target.value.toLowerCase());

  const handleSort = (key) => setSortKey(key);

  const handleFilter = (e) => {
    const { name, value } = e.target;
    setFilter((prev) => ({ ...prev, [name]: value }));
  };

  const filteredBorrows = borrows
    .filter((borrow) =>
      borrow.clientId.toLowerCase().includes(searchTerm) &&
      (!filter.clientId || borrow.clientId === filter.clientId) &&
      (!filter.mediaId || borrow.mediaId === filter.mediaId)
    )
    .sort((a, b) => (a[sortKey] > b[sortKey] ? 1 : -1));

  return (
    <div>
      <div style={{ display: 'flex', justifyContent: 'space-between' }}>
        <input placeholder="Search by Client or Media" onChange={handleSearch} />
        <button onClick={() => window.location.href = '/borrow/initiate'}>Initiate Borrow</button>
      </div>
      <div>
        <input name="clientId" placeholder="Filter by Client" onChange={handleFilter} />
        <input name="mediaId" placeholder="Filter by Media" onChange={handleFilter} />
        <select onChange={(e) => handleSort(e.target.value)}>
          <option value="borrowDate">Sort by Borrow Date</option>
          <option value="returnDate">Sort by Return Date</option>
          <option value="totalPrice">Sort by Price</option>
        </select>
      </div>
      <ul>
        {filteredBorrows.map((borrow) => (
          <li key={borrow.borrowId}>
            {`Client: ${borrow.clientId} | Media: ${borrow.mediaId} | Borrowed: ${new Date(borrow.borrowDate).toLocaleDateString()} | Return Date: ${new Date(borrow.returnDate).toLocaleDateString()} | Price: $${borrow.totalPrice.toFixed(2)}`}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default BorrowLister;
