import React, { useState, useEffect } from 'react';
import ClientService from './ClientService'; // Adjust the import based on your project structure

const ClientLister = () => {
  const [clients, setClients] = useState([]);
  const [filters, setFilters] = useState({
    name: '',
    address: '',
    email: ''
  });
  const [sortOrder, setSortOrder] = useState('asc'); // Default sort order

  // Fetch data with filters and sorting
  const fetchClients = () => {
    ClientService.get(filters, sortOrder)
      .then(response => setClients(response.data))
      .catch(error => console.error(error));
  };

  // Run the fetch whenever filters or sortOrder changes
  useEffect(() => {
    fetchClients();
  }, [filters, sortOrder]);

  const handleDelete = (clientId) => {
    ClientService.delete(clientId)
      .then(() => {
        // Remove the deleted client from state (UI update)
        setClients(clients.filter(client => client.clientId !== clientId));
      })
      .catch(error => console.error("Error deleting client:", error));
  };

  return (
    <div className="list-page">
      {/* Filter Controls */}
      <div className="filter-container">
        <div className="filter-box">
          <label className="filter-label">Search by Name</label>
          <input
            type="text"
            className="filter-input"
            value={filters.name}
            onChange={e => setFilters({ ...filters, name: e.target.value })}
          />
        </div>
        <div className="filter-box">
          <label className="filter-label">Search by Address</label>
          <input
            type="text"
            className="filter-input"
            value={filters.address}
            onChange={e => setFilters({ ...filters, address: e.target.value })}
          />
        </div>
        <div className="filter-box">
          <label className="filter-label">Search by Email</label>
          <input
            type="email"
            className="filter-input"
            value={filters.email}
            onChange={e => setFilters({ ...filters, email: e.target.value })}
          />
        </div>
        <button className="sort-button" onClick={() => setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc')}>
          {sortOrder === 'asc' ? 'Sort Descending' : 'Sort Ascending'}
        </button>
      </div>

      {/* Table */}
      <div className="table">
        {clients.map(client => (
          <div key={client.clientId} className="table-row">
            <div className="form-field">{client.name}</div>
            <div className="form-field">{client.address}</div>
            <div className="form-field">{client.email}</div>
            <div className="form-field">{client.phoneNumber}</div>
            <div className="form-field">
              <button className="modify-button">Edit</button>
              <button className="delete-button" onClick={() => handleDelete(client.clientId)}>Delete</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ClientLister;
