import React, { useState } from 'react';
import BorrowsService from './BorrowsService';

const BorrowInitiator = () => {
  const [borrow, setBorrow] = useState({
    clientId: '',
    mediaId: '',
    borrowDate: '',
    returnDate: '',
    returned: false,
  });

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setBorrow((prev) => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await BorrowsService.create(borrow); // Initiates the borrow
      alert('Borrow initiated successfully');
    } catch (error) {
      console.error('Error initiating borrow: ', error);
      alert('Error initiating borrow');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        name="clientId"
        placeholder="Client ID"
        required
        value={borrow.clientId}
        onChange={handleChange}
      />
      <input
        name="mediaId"
        placeholder="Media ID"
        required
        value={borrow.mediaId}
        onChange={handleChange}
      />
      <input
        name="borrowDate"
        type="datetime-local"
        placeholder="Borrow Date"
        required
        value={borrow.borrowDate}
        onChange={handleChange}
      />
      <input
        name="returnDate"
        type="datetime-local"
        placeholder="Return Date"
        required
        value={borrow.returnDate}
        onChange={handleChange}
      />
      <label>
        Returned:
        <input
          name="returned"
          type="checkbox"
          checked={borrow.returned}
          onChange={handleChange}
        />
      </label>
      <button type="submit">Initiate Borrow</button>
    </form>
  );
};

export default BorrowInitiator;
