import React, { useState, useEffect } from 'react';
import MediaService from './MediaService'; // Adjust the import based on your project structure

const MediaLister = () => {
  const [medias, setMedias] = useState([]);
  const [filters, setFilters] = useState({
    name: '',
    genre: '',
    rating: ''
  });
  const [sortOrder, setSortOrder] = useState('asc'); // Default sort order

  // Fetch data with filters and sorting
  const fetchMedias = () => {
    MediaService.getAll(filters, sortOrder)
      .then(response => setMedias(response.data))
      .catch(error => console.error(error));
  };

  // Run the fetch whenever filters or sortOrder changes
  useEffect(() => {
    fetchMedias();
  }, [filters, sortOrder]);

  const handleDelete = (mediaId) => {
    MediaService.delete(mediaId)
      .then(() => {
        // Remove the deleted media from state (UI update)
        setMedias(medias.filter(media => media.mediaId !== mediaId));
      })
      .catch(error => console.error("Error deleting media:", error));
  };

  return (
    <div className="list-page">
      {/* Filter Controls */}
      <div className="filter-container">
        <div className="filter-box">
          <label className="filter-label">Search by Name</label>
          <input
            type="text"
            className="filter-input"
            value={filters.name}
            onChange={e => setFilters({ ...filters, name: e.target.value })}
          />
        </div>
        <div className="filter-box">
          <label className="filter-label">Search by Genre</label>
          <input
            type="text"
            className="filter-input"
            value={filters.genre}
            onChange={e => setFilters({ ...filters, genre: e.target.value })}
          />
        </div>
        <div className="filter-box">
          <label className="filter-label">Search by Rating</label>
          <input
            type="number"
            className="filter-input"
            value={filters.rating}
            onChange={e => setFilters({ ...filters, rating: e.target.value })}
          />
        </div>
        <button className="sort-button" onClick={() => setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc')}>
          {sortOrder === 'asc' ? 'Sort Descending' : 'Sort Ascending'}
        </button>
      </div>

      {/* Table */}
      <div className="table">
        {medias.map(media => (
          <div key={media.mediaId} className="table-row">
            <div className="form-field">{media.name}</div>
            <div className="form-field">{media.genre}</div>
            <div className="form-field">{media.rating}</div>
            <div className="form-field">{media.duration}</div>
            <div className="form-field">
              <button className="modify-button">Edit</button>
              <button className="delete-button" onClick={() => handleDelete(media.mediaId)}>Delete</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default MediaLister;
