import React, { useEffect, useState } from 'react';
import MediasService from './MediaService';

function MediaLister() {
  const [medias, setMedias] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [sortKey, setSortKey] = useState('name');
  const [filter, setFilter] = useState({ genre: '', type: '', rating: '' });

  useEffect(() => {
    MediasService.getAll().then((response) => setMedias(response.data));
  }, []);

  const handleSearch = (e) => setSearchTerm(e.target.value.toLowerCase());

  const handleSort = (key) => setSortKey(key);

  const handleFilter = (e) => {
    const { name, value } = e.target;
    setFilter((prev) => ({ ...prev, [name]: value }));
  };

  const filteredMedia = medias
    .filter((media) =>
      media.name.toLowerCase().includes(searchTerm) &&
      (!filter.genre || media.genre === filter.genre) &&
      (!filter.type || media.type === filter.type) &&
      (!filter.rating || media.rating === parseInt(filter.rating))
    )
    .sort((a, b) => (a[sortKey] > b[sortKey] ? 1 : -1));

  return (
    <div>
      <div style={{ display: 'flex', justifyContent: 'space-between' }}>
        <input placeholder="Search by name" onChange={handleSearch} />
        <button onClick={() => window.location.href = '/media/add'}>Add New Media</button>
      </div>
      <div>
        <select name="genre" onChange={handleFilter}>
          <option value="">All Genres</option>
          <option value="Genre1">Genre1</option>
          <option value="Genre2">Genre2</option>
        </select>
        
        <input type="number" name="rating" placeholder="Rating" onChange={handleFilter} />
        <select onChange={(e) => handleSort(e.target.value)}>
          <option value="name">Sort by Name</option>
          <option value="genre">Sort by Genre</option>
          <option value="type">Sort by Type</option>
          <option value="rating">Sort by Rating</option>
        </select>
      </div>
      <ul>
        {filteredMedia.map((media) => (
          <li key={media.mediaId}>
            {media.name} - {media.genre} - {media.type} - Rating: {media.rating}
            <button onClick={() => window.location.href = `/media/edit/${media.mediaId}`}>Modify</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default MediaLister;
