// src/Media/MediaLister.js
import React, { useState, useEffect } from 'react';
import MediaService from './MediaService';
import ListHeader from '../components/ListHeader';
import '../styles/ListPage.css';
import '../App.css'

const MediaLister = () => {
    const [mediaList, setMediaList] = useState([]);

    useEffect(() => {
        MediaService.getAll().then((response) => setMediaList(response.data));
    }, []);

    return (
        <div className="list-page">
            <ListHeader
                headers={[
                    { key: 'name', label: 'Name' },
                    { key: 'genre', label: 'Genre' },
                    { key: 'rating', label: 'Rating' },
                ]}
                onSort={() => {}}
                onFilter={() => {}}
            />
            <div className="table">
                {mediaList.map((media) => (
                    <div className="table-row" key={media.mediaId}>
                        <div>{media.name}</div>
                        <div>{media.genre}</div>
                        <div>{media.rating}</div>
                        <div>
                            <button onClick={() => MediaService.delete(media.mediaId)}>Delete</button>
                            <button onClick={() => window.location.href = `/media/update/${media.mediaId}`}>Edit</button>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default MediaLister;
