import React, { useEffect, useState } from 'react';
import ClientsService from './ClientsService';

function ClientLister() {
  const [clients, setClients] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [sortKey, setSortKey] = useState('name');
  const [filter, setFilter] = useState({ address: '', email: '' });

  useEffect(() => {
    ClientsService.getAll().then((response) => setClients(response.data));
  }, []);

  const handleSearch = (e) => setSearchTerm(e.target.value.toLowerCase());

  const handleSort = (key) => setSortKey(key);

  const handleFilter = (e) => {
    const { name, value } = e.target;
    setFilter((prev) => ({ ...prev, [name]: value }));
  };

  const filteredClients = clients
    .filter((client) =>
      client.name.toLowerCase().includes(searchTerm) &&
      (!filter.address || client.address.includes(filter.address)) &&
      (!filter.email || client.email.includes(filter.email))
    )
    .sort((a, b) => (a[sortKey] > b[sortKey] ? 1 : -1));

  return (
    <div>
      <div style={{ display: 'flex', justifyContent: 'space-between' }}>
        <input placeholder="Search by name" onChange={handleSearch} />
        <button onClick={() => window.location.href = '/client/new'}>Add New Client</button>
      </div>
      <div>
        <input
          name="address"
          placeholder="Filter by Address"
          onChange={handleFilter}
        />
        <input
          name="email"
          placeholder="Filter by Email"
          onChange={handleFilter}
        />
        <select onChange={(e) => handleSort(e.target.value)}>
          <option value="name">Sort by Name</option>
          <option value="address">Sort by Address</option>
          <option value="email">Sort by Email</option>
        </select>
      </div>
      <ul>
        {filteredClients.map((client) => (
          <li key={client.clientId}>
            {client.name} - {client.address} - {client.email}
            <button onClick={() => window.location.href = `/client/edit/${client.clientId}`}>Modify</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default ClientLister;
